import React, { useState, useEffect } from 'react';
import CommonSection from '../shared/CommonSection';

import '../styles/fruit.css';
import FruitData from '../assets/data/fruits';
import FruitCard from './../shared/FruitCard';
import SearchBar from '../shared/SearchBar';
import Newsletter from '../shared/Newsletter';
import { Container, Row, Col } from 'reactstrap';
import useFetch from '../hooks/useFetch';
import {BASE_URL} from '../untils/config'


const Fruits = () => {
  const [pageCount, setPageCount] = useState(0);
  const [page, setPage] = useState(0);

  const {data:fruits,loading, error} = useEffect(`${BASE_URl}/fruits`)
  const {data:fruitCount} = useEffect(`${BASE_URL}/fruits/search/getFruitCount`)

  useEffect(() => {
    const pages = Math.ceil(fruitCount / 4); 
    setPageCount(pages);
  }, [page, fruitCount]);

  return (
    <>
      <CommonSection title={'All Fruit'}></CommonSection>
      <section>
        <Container>
          <Row>
            <SearchBar />
          </Row>
        </Container>
      </section>
      <section className="pt-0">
        <Container>
          {
            !loading && !error
          }
        </Container>
      </section>
      <Newsletter />
    </>
  );
};

export default Fruits;
