import React from 'react';
import '../styles/fruit-details.css';

import { Container, Row, Col, Form, ListGroup } from 'reactstrap';
import { useParams } from 'react-router-dom';
import FruitData from '../assets/data/fruits';
import calculateAvgRating from '../untils/avgRating';
const FruitDetails = () => {
  const { id } = useParams();
  //this is an static data later we will call our API and load our data from database
  const tour = FruitData.find((tour) => tour.id == id);

  //destructure properties from tour object

  const {
    photo,
    title,
    desc,
    price,
    address,
    reviews,
    city,
    distance,
    maxGroupSize,
  } = tour;

  const { totalRating, avgRating } = calculateAvgRating(reviews);

  return (
    <>
      <section>
        <Container>
          <Row>
            <Col lg="8">
              <div className="fruit__content">
                <img src={photo} alt="" />
                <div className="fruit__info">
                  <h2>{title}</h2>

                  <div className="d-flex align-items-center gap-5">
                    <span className="d-flex align-items-center gap-1">
                      <span className="fruit__rating d-flex align-items-center gap-1">
                        <i
                          class="ri-star-fill"
                          style={{ color: 'var(--secondary -color)' }}
                        ></i>{' '}
                        {calculateAvgRating === 0 ? null : avgRating}
                        {totalRating === 0 ? (
                          'Not rated'
                        ) : (
                          <span>({reviews?.length})</span>
                        )}
                      </span>
                    </span>
                    <span>
                      <i class="ri-map-pin-user-fill"></i> {address}
                    </span>
                  </div>


                  <div className="fruit__extra-details">
                    <span>
                    <i class="ri-map-pin-2-line">{city}</i>
                    </span>

                  </div>
                </div>
              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </>
  );
};

export default FruitDetails;
