import React, { useState, useEffect } from 'react';
import CommonSection from '../shared/CommonSection';

import '../styles/fruit.css';
import FruitData from '../assets/data/fruits';
import FruitCard from './../shared/FruitCard';
import SearchBar from '../shared/SearchBar';
import Newsletter from '../shared/Newsletter';
import { Container, Row, Col } from 'reactstrap';
import useFetch from '../hooks/useFetch';
import {BASE_URL} from '../untils/config'


const Fruits = () => {
  const [pageCount, setPageCount] = useState(0);
  const [page, setPage] = useState(0);
  const {data:fruits} = useEffect(`${BASE_URl}/fruits`)
  const {data:fruitCount} = useEffect(`${BASE_URL}/fruit`)

  useEffect(() => {
    const pages = Math.ceil(5 / 4); //later we will use backend date count
    setPageCount(pages);
  }, [page]);

  return (
    <>
      <CommonSection title={'All Fruit'}></CommonSection>
      <section>
        <Container>
          <Row>
            <SearchBar />
          </Row>
        </Container>
      </section>
      <section className="pt-0">
        <Container>
          <Row>
            {FruitData?.map((fruit) => (
              <Col lg="3" className="mb-4" key={fruit.id}>
                <FruitCard tour={fruit} />
              </Col>
            ))}
            <Col lg="12">
              <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
                {[...Array(pageCount).keys()].map((number) => (
                  <span
                    key={number}
                    onClick={() => setPage(number)}
                    className={page === number ? 'active__page' : ''}
                  >
                    {number + 1}
                  </span>
                ))}
              </div>
            </Col>
          </Row>
        </Container>
      </section>
      <Newsletter />
    </>
  );
};

export default Fruits;
