import React from 'react';
import Slider from 'react-slick';
import ava01 from '../../assets/images/gallery-01.jfif';
import ava02 from '../../assets/images/gallery-04.jfif';
import ava03 from '../../assets/images/gallery-06.jfif';

const Testimonials = () => {
  const settings = {
    dots: true,
    infinite: true,
    autoplay: true,
    speed: 1000,
    swipeToSlide: true,
    autoplaySpeed: 2000,
    slidesToShow: 3,
    responsive: [
      {
        breakpoint: 992,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 576,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
        },
      },
    ],
  };

  return (
    <Slider {...settings}>
      <div className="testimonial py-4 px-3">
        <p>Đu đủ là loại quả dễ trồng nhất tại </p>
        <div className="d-flex align-items-center gap-4 mt-3">
          <img src={ava01} className="w-25 h-25 rounded-2" alt="" />
          <div>
            <h6 className="mb-0 mt-3">Nhật Duật</h6>
            <p>Chủ Shop</p>
          </div>
        </div>
      </div>
      <div className="testimonial py-4 px-3">
        <p>Bạn mua hàng thân hàng với 2 năm tại shop</p>
        <div className="d-flex align-items-center gap-4 mt-3">
          <img src={ava02} className="w-25 h-25 rounded-2" alt="" />
          <div>
            <h6 className="mb-0 mt-3">Trần Phương</h6>
            <p>Khách hàng</p>
          </div>
        </div>
      </div>
      <div className="testimonial py-4 px-3">
        <p>Bé học sinh mua hàng</p>
        <div className="d-flex align-items-center gap-4 mt-3">
          <img src={ava03} className="w-25 h-25 rounded-2" alt="" />
          <div>
            <h6 className="mb-0 mt-3">Ánh Hồng</h6>
            <p>Khách hàng</p>
          </div>
        </div>
      </div>
      <div className="testimonial py-4 px-3">
        <p>Bạn  mua hàng thân hàng với 2 năm tại shop</p>
        <div className="d-flex align-items-center gap-4 mt-3">
          <img src={ava02} className="w-25 h-25 rounded-2" alt="" />
          <div>
            <h6 className="mb-0 mt-3">Trần Phương</h6>
            <p>Khách hàng</p>
          </div>
        </div>
      </div>
    </Slider>
  );
};

export default Testimonials;
