import React, { useRef, useState } from 'react';
import './search-bar.css';
import { Col, Form, FormGroup } from 'reactstrap';

import { BASE_URL } from './../untils/config';
import { useNavigate } from 'react-router-dom';

const SearchBar = () => {
  const TraicaytuoingonRef = useRef('');
  const GiaohangRef = useRef(0);
  const SoluongRef = useRef(0);
  const navigate = useNavigate();
  // const [soluong, setSoLuong] = useState([]);
  const searchTraicay = async () => {
    const Traicaytuoingon = TraicaytuoingonRef.current.value;
    const Giaohang = GiaohangRef.current.value;
    const Soluong = SoluongRef.current.value;

    if (Traicaytuoingon === '' || Giaohang === '' || Soluong === '') {
      return alert('Bạn phải nhập đủ các yêu cầu!');
    }

    const res = await fetch(
      `${BASE_URL}/fruits/search/getFruitBySearch?city=${Traicaytuoingon}&Giaohang=${Giaohang}&Soluong=${Soluong}`
    );

    if (!res.ok) alert('Something went wrong');

    const result = await res.json();
    // console.log('mmm', soluong);
    navigate(
      `/fruits/search?city=${Traicaytuoingon}&Giaohang=${Giaohang}&Soluong=${Soluong}`,
      { state: result.data }
    );
  };

  return (
    <Col lg="12">
      <div className="search__bar">
        <Form className="d-flex algin-items-center gap-4">
          <FormGroup className="d-flex gap-3 form__group form__group-fast ">
            <span>
              <i class="ri-heart-pulse-fill"></i>
            </span>
            <div>
              <h6>Trái cây tươi ngon</h6>
              <input
                type="text"
                placeholder="Loại trái cây"
                ref={TraicaytuoingonRef}
              />
            </div>
          </FormGroup>
          <FormGroup className="d-flex gap-3 form__group form__group-fast ">
            <span>
              <i class="ri-takeaway-fill"></i>
            </span>
            <div>
              <h6>Giao hàng</h6>
              <input type="number" placeholder="Trong 4h" ref={GiaohangRef} />
            </div>
          </FormGroup>
          <FormGroup className="d-flex gap-3 form__group form__group-last ">
            <span>
              <i class="ri-shopping-cart-line"></i>
            </span>
            <div>
              <h6>Số lượng</h6>
              <input
                type="number"
                placeholder="0"
                ref={SoluongRef}
                // value={soluong}
                // onChange={(e) => {
                //   setSoLuong(e.target.value);
                // }}
              />
            </div>
          </FormGroup>
          <span className="search__icon" type="submit" onClick={searchTraicay}>
            <i class="ri-search-line"></i>
          </span>
        </Form>
      </div>
    </Col>
  );
};

export default SearchBar;
