import React, { useState, useEffect } from 'react';
import CommonSection from '../shared/CommonSection';

import '../styles/fruit.css';
import FruitData from '../assets/data/fruits';
import Newsletter from '../shared/Newsletter';
import SearchBar from '../shared/SearchBar';
import FruitCard from '../shared/FruitCard';
import { Col, Container, Row } from 'reactstrap';

const Fruit = () => {
  const [pageCount, setPageCount] = useEffect(0);
  const [page, setPage] = useState(0);

  useEffect(() => {
    const pages = Math.ceil(5 / 8); //later we will use backend date count
    setPageCount(pages);
  }, [page]);

  return (
    <>
      <CommonSection title={'All Fruit'}></CommonSection>
      <section>
        <Container>
          <Row>
            <SearchBar />
          </Row>
        </Container>
      </section>
      <section className="pt-0">
        <Container>
          <Row>
            {FruitData?.map((tour) => (
              <Col lg="3" className="mb-4" key={tour.id}>
                <FruitCard tour={tour} />
              </Col>
            ))}
            <Col lg="12">
              <div className="pagination d-flex align-items-center justify-content-center mt-4 gap-3">
              {[...Array(pageCount).keys()].map(n)}
         
              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </>
  );
};

export default Fruit;
